document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".page__content, .article__content");if(!e)return;const s=document.createTreeWalker(e,NodeFilter.SHOW_TEXT,{acceptNode:function(e){const t=e.parentElement;return t.tagName==="SCRIPT"||t.tagName==="STYLE"||t.tagName==="CODE"||t.closest("pre")?NodeFilter.FILTER_REJECT:e.nodeValue.includes("++")?NodeFilter.FILTER_ACCEPT:NodeFilter.FILTER_REJECT}}),t=[];let n;for(;n=s.nextNode();)t.push(n);t.forEach(e=>{const t=e.nodeValue,n=/\+\+(.+?)\+\+/g;if(n.test(t)){const o=t.replace(/\+\+(.+?)\+\+/g,'<span class="underline-text">$1</span>'),n=document.createElement("div");n.innerHTML=o;const s=e.parentNode;for(;n.firstChild;)s.insertBefore(n.firstChild,e);s.removeChild(e)}})})